curDate = curDate.getHours();
let greeting = '';
const cssStyle = { };

if (curDate>=0 && curDate<=11)
{
    greeting = 'Good Morning';
    cssStyle.color = 'green';
} 
else if (curDate >=12 && curDate <=7)
{

  greeting = 'Good Afternoon';
  cssStyle.color = 'orange';
}

else{
    greeting = 'Good Night';
    cssStyle.color = 'red';

}

ReactDom.render(
  <React.Fragment>

  <div>


  </div>

